mapper.select:
select user_id, user_name, user_type, user_Age
from user_tb
where user_id = #{userid} and user_pw = #{userpw}

select user_id, user_pw, user_name, user_email, user_age, user_type
from user_tb
where user_id = #{userId}

mapper.update:
update user_tb
user_name = #{userName}, user_age = #{userAge}, user_pw = #{userPw}, user_email = #{userEmail},
where user_id = #{userId}
